package swa.manage.entity;import swa.manage.entity.vo.SearchVo;import java.io.Serializable;import java.util.Date;/** * RoomConfig:配置表 * Created by jinyan.cao on 2017-07-23 21:38:44 */public class RoomConfig implements Serializable {    private static final long serialVersionUID = 1L;    /**     * 自增主键     */    private Long id;    /**     * 所在城市     */    private String city;    /**     * 所属区域     */    private String region;    /**     * 所在楼层     */    private String layer;    /**     * 房间名称     */    private String roomName;    /**     * 可用状态：0-不可用，1-可用     */    private Integer validStatus;    /**     * 创建时间     */    private Date createTime;    /**     * 更新时间     */    private Date updateTime;    public RoomConfig() {    }    /**     * 构造查询条件参数     *     * @param searchVo     */    public RoomConfig(SearchVo searchVo) {        this.city = searchVo.getCity();        this.region = searchVo.getRegion();    }    public Long getId() {        return this.id;    }    public void setId(Long id) {        this.id = id;    }    public String getCity() {        return this.city;    }    public void setCity(String city) {        this.city = city;    }    public String getRegion() {        return this.region;    }    public void setRegion(String region) {        this.region = region;    }    public String getLayer() {        return this.layer;    }    public void setLayer(String layer) {        this.layer = layer;    }    public String getRoomName() {        return this.roomName;    }    public void setRoomName(String roomName) {        this.roomName = roomName;    }    public Integer getValidStatus() {        return this.validStatus;    }    public void setValidStatus(Integer validStatus) {        this.validStatus = validStatus;    }    public Date getCreateTime() {        return this.createTime;    }    public void setCreateTime(Date createTime) {        this.createTime = createTime;    }    public Date getUpdateTime() {        return this.updateTime;    }    public void setUpdateTime(Date updateTime) {        this.updateTime = updateTime;    }    @Override    public String toString() {        return "RoomConfig{" +                "id=" + id +                ", city='" + city + '\'' +                ", region='" + region + '\'' +                ", layer='" + layer + '\'' +                ", roomName='" + roomName + '\'' +                ", validStatus=" + validStatus +                ", createTime=" + createTime +                ", updateTime=" + updateTime +                '}';    }}