package swa.manage.entity;import java.io.Serializable;import java.util.Date;/** * StaffVerify:登录用户信息表 * Created by jinyan.cao on 2017-08-03 14:15:34 */public class StaffVerify implements Serializable {    private static final long serialVersionUID = 5885320803869854676L;    /**     * 自增主键     */    private Long id;    /**     * 用户名     */    private String userName;    /**     * 密码     */    private String password;    /**     * 可用：0-不可用，1-可用     */    private Integer validStatus;    /**     * 创建时间     */    private Date createTime;    /**     * 更新时间     */    private Date updateTime;    public Long getId() {        return this.id;    }    public void setId(Long id) {        this.id = id;    }    public String getUserName() {        return this.userName;    }    public void setUserName(String userName) {        this.userName = userName;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public Integer getValidStatus() {        return this.validStatus;    }    public void setValidStatus(Integer validStatus) {        this.validStatus = validStatus;    }    public Date getCreateTime() {        return this.createTime;    }    public void setCreateTime(Date createTime) {        this.createTime = createTime;    }    public Date getUpdateTime() {        return this.updateTime;    }    public void setUpdateTime(Date updateTime) {        this.updateTime = updateTime;    }    @Override    public String toString() {        return "StaffVerify{" +                "id=" + id +                ", userName='" + userName + '\'' +                ", password='" + password + '\'' +                ", validStatus=" + validStatus +                ", createTime=" + createTime +                ", updateTime=" + updateTime +                '}';    }}